def minion_game(string):
    string = string.upper()

    fullListKevin = []
    fullListStuart = []
    s = []

    sb_len = 1
    lString = list(string)
    sString = set(string)
    sString = list(sString)
    kevingPoints = 0
    stuartPoints = 0

    #Possible substrings from left to right
    while sb_len != len(string)+1:
        for i in range(len(string)+1):
            if len(string[i:i+sb_len]) < sb_len:
                pass
            else:
                s = list(string[i:i+sb_len])
                if s[0] in ('AEIOU'):
                    fullListKevin.append(string[i:i+sb_len])
                else:
                    fullListStuart.append(string[i:i + sb_len])
        sb_len += 1

    #Count the scores
    setlistKevin = set(fullListKevin)
    setlistKevin = list(setlistKevin)
    setlistStuart = set(fullListStuart)
    setlistStuart = list(setlistStuart)

    for i in range(len(setlistKevin)):
        kevingPoints += fullListKevin.count(setlistKevin[i])

    for i in range(len(setlistStuart)):
        stuartPoints += fullListStuart.count(setlistStuart[i])

    if kevingPoints < stuartPoints:
        print('Stuart {0}'.format(stuartPoints))
    elif kevingPoints == stuartPoints:
        print('Draw')
    else:
        print('Kevin {0}'.format(kevingPoints))

minion_game('')